import React, { useState, useEffect } from "react";
import {
  Words,
  CategorySelection,
  HangmanDisplay,
  HealthBar,
  Keyboard,
  Toggle,
  logo,
  playButton,
  backButton,
} from './components/Import';
import {
  selectCategory,
  handleGuess,
  goToHome,
  changeCategory,
  continueGame,
  quitGame,
  playAgain,
} from './functions';

const App = () => {
  const [page, setPage] = useState("home");
  const [category, setCategory] = useState("");
  const [word, setWord] = useState("");
  const [guessedLetters, setGuessedLetters] = useState([]);
  const [health, setHealth] = useState(100);
  const [menuOpen, setMenuOpen] = useState(false);
  const [gameOver, setGameOver] = useState(false);
  const [lostWord, setLostWord] = useState("");
  const [hasWon, setHasWon] = useState(false);

  const selectCategory = (category) => {
    setCategory(category);
    const words = Words[category];
    const randomWord = words[Math.floor(Math.random() * words.length)];
    setWord(randomWord.replace(/_/g, ' '));
    setGuessedLetters([]);
    setHealth(100);
    setPage("game");
  };

  const handleGuess = (letter) => {
    if (guessedLetters.includes(letter) || gameOver || hasWon) return;
    setGuessedLetters([...guessedLetters, letter]);

    if (!word.includes(letter)) {
      setHealth(health - 100 / 6);
      if (health - 100 / 6 <= 0) {
        setGameOver(true);
        setLostWord(word);
      }
    }
  };

  useEffect(() => {
    if (
      word &&
      word.split("").every((letter) => letter === ' ' || guessedLetters.includes(letter))
    ) {
      setHasWon(true);
    }
  }, [guessedLetters, word]);

  const goToHome = () => {
    setPage("home");
    setMenuOpen(false);
    setGameOver(false);
    setHasWon(false);
  };

  const changeCategory = () => {
    setPage("category");
    setMenuOpen(false);
    setGameOver(false);
    setHasWon(false);
  };

  const continueGame = () => {
    setMenuOpen(false);
  };

  const quitGame = () => {
    setPage("home");
    setMenuOpen(false);
    setGameOver(false);
    setHasWon(false);
  };

  const playAgain = () => {
    setWord("");
    setGuessedLetters([]);
    setHealth(100);
    setGameOver(false);
    setHasWon(false);
    setPage("category");
  };

  return (
    <div className="container">
      {page === "home" ? (
        <div className="homepage-container">
          <div className="homepage">
            <img className="homepage-logo" src={logo} alt="" />
            <button
              className="homepage-button"
              onClick={() => setPage("category")}
            >
              <img src={playButton} alt="Play Button" />
            </button>
          </div>
        </div>
      ) : page === "category" ? (
        <div className="category-container">
          <div className="category-header">
            <button className="category-buttons" onClick={goToHome}>
              <img
                className="category-back-button"
                src={backButton}
                alt="Back Button"
              />
            </button>
            <h1>Select Category</h1>
          </div>
          <CategorySelection selectCategory={selectCategory} />
        </div>
      ) : (
        <div className="in-game-container">
          {gameOver && (
            <div className="popup-container">
              <div className="popup">
                <h2>You've Lost!</h2>
                <p>Incorrect word: {lostWord}</p>
                <div className="popup-buttons">
                  <button onClick={playAgain}>Play Again</button>
                  <button onClick={quitGame}>Quit Game</button>
                </div>
              </div>
            </div>
          )}
          {hasWon && (
            <div className="popup-container">
              <div className="popup">
                <h2>You've Won!</h2>
                <p>Correct word: {word}</p>
                <div className="popup-buttons">
                  <button onClick={playAgain}>Play Again</button>
                  <button onClick={quitGame}>Quit Game</button>
                </div>
              </div>
            </div>
          )}
          <div className="in-game-buttons-and-health">
            <div className="in-game-header">
              <Toggle
                onContinue={continueGame}
                newCategory={changeCategory}
                quitGame={quitGame}
                menuOpen={menuOpen}
                setMenuOpen={setMenuOpen}
              />
              <div className="category-display">{category}</div>
            </div>
            <HealthBar health={health} />
          </div>
          <div className="hangman-and-keyboard">
            <HangmanDisplay word={word} guessedLetters={guessedLetters} />
            <Keyboard
              guessedLetters={guessedLetters}
              handleGuess={handleGuess}
            />
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
